create table concert (capacity integer not null,price integer not null,reserved_count integer not null,id bigint generated by default as identity,concert_date varchar(255) not null, title varchar(255) not null, primary key (id))
create table ongoing (created_at timestamp(6),id bigint generated by default as identity,user_id bigint,status varchar(255) not null,primary key (id))
create table payment (created_at timestamp(6),payment_id bigint generated by default as identity,user_id bigint not null,reservation_number varchar(255) not null,primary key (payment_id))
create table reservation (seat_num integer not null,created_at timestamp(6),reservation_id bigint generated by default as identity,user_id bigint not null,final_confirm varchar(255) not null,reservation_date varchar(255) not null,reservation_number varchar(255) not null unique,title varchar(255) not null,primary key (reservation_id))
create table users (point integer not null,user_id bigint generated by default as identity,primary key (user_id))
create table waiting (created_at timestamp(6),id bigint generated by default as identity,user_id bigint,status varchar(255) not null,primary key (id)



